INSERT INTO `ig_hubungi_kami` (`id_hubungi_kami`, `nama`, `email`, `no_telp`, `subjek`, `isi_pesan`) VALUES
(1, 'Teddy', 'teddy@lyto.net', '08568196437', 'Tidak terima email konfirmasi', 'Halo\r\n\r\nMau minta bantuannya untuk cek id teddy@lyto.net\r\nSaya sudah daftar id tapi tidak pernah terima email untuk mengaktifkan akun. Bisa tolong dibantu untuk diaktifkan.\r\n\r\nBila coba login, maka muncul pesan error dibawah ini\r\nCek Email anda untuk mengaktifkan akun anda!\r\n\r\nTerus saya coba cek reset password dan terima link reset password di email saya,'),
(2, 'Rahmat', 'doplank@live.com', '081215864246', 'Loker', 'Hai\r\n\r\nApa ada loker sebagai penguji kelayakan dan pemberi rating game?\r\n\r\nSaya gamer serta sebagai masyarat umum ingin terjun ke dalam ranah rating game Indonesia agar bisa maju seperti di luar negeri.\r\n\r\nTerima kasih.'),
(3, 'Jonathan Julion', 'jonathanjulion@yahoo.com', '081221001225', 'Mengenai Game Luar Indonesia ', 'Halo IGRS, mau tanya nih \r\n1. bagaimana dengan game luar Indonesia sebagai contoh dota , counter strike apakah akan diklasifikasikan sesuai dengan IGRS? \r\n2. atau Dev luar tersebut harus mendaftarkan gamenya ke IGRS? \r\n3. andai Dev tidak mendaftarkannya apakah game tersebut dilarang persebarannya di Indonesia?\r\n4. Terdapat Steam yang menjual game secara online dan mudah untuk downloadnya menggunakan sistim rating luar ESRB, berlakukah ESRB di Indonesia\r\n\r\nada rasa keingintahuan saya seputar sistim rating game di Indonesia, jika memungkinkan bisakah minta waktu IGRS untuk diwawancara? \r\n\r\nsebelumnya saya Jonathan Julion dari Institut Teknologi Harapan Bangsa, Bandung, dalam penelitian untuk Karya Desain mengenai Sistim Rating Game di Indonesia\r\n\r\nthank you'),
(4, 'Cepy', 'cepy@lyto.net', '081290803000', 'Edit Aplikasi', 'Halo, Saya mau nanya kalo sudah submit game/aplikasi dan udah publish di direktori, apakah ada cara untuk edit kembali? soalnya ada beberapa konten yang salah.'),
(5, 'Tubagus Angga', 'tubagus@gravindo.id', '081285324242', 'Hapus Konten Game Rating', 'Saya ingin meminta bantuannya untuk menghapus konten game rating dari game Ragnarok Online (http://igrs.id/aplikasi/ragnarok-online), \r\nkarena saya ingin mengubah beberapa ketentuan di bagian pilihan Ratingnya.\r\nSaat ini Rating Game Ragnarok Online tergabung di kelompok usia 3Thn ke atas, saya ingi mengubahnya menjadi tergabung di kelompok usia 13Thn ke atas.\r\nTerima Kasih'),
(6, 'Tubagus Angga', 'tubagus@gravindo.id', '081285324242', 'Hapus Konten Game Rating', 'Selamat Sore Tim IGRS,\r\n \r\nMohon bantuannya sekali lagi untuk menghapus Self Rating untuk Ragnarok Online.\r\n\r\n\r\nTerima Kasih,\r\nTubagus Angga'),
(7, 'Teddy', 'teddy@lyto.net', '08568196437', 'Hapus Aplikasi', 'Halo\r\n\r\nMau minta donk hapus untuk assessment yang dibawah donk karena kita mau ubah tapi kita ga bisa edit lagi. Jadi dihapus saja trus nanti kita tambah baru aja. Untuk id kita yang terdaftar di igrs yaitu teddy@lyto.net\r\n1. ROBOWAR - Robot VS Alien\r\n2. Fantasia Heroes\r\n3. Pixel Super Heroes\r\n4. Heaven Knights\r\n5. Kafe Impian\r\n6. Juragan Ojek'),
(8, 'Teddy', 'teddy@lyto.net', '08568196437', 'Hapus Konten Game Rating ', 'Saya ingin meminta bantuannya untuk menghapus konten game rating dari game:\r\nRF Classic (http://igrs.id/aplikasi/rf-classic)\r\nPerfect World (http://igrs.id/aplikasi/perfect-world)\r\nRF Online (http://igrs.id/aplikasi/rf-online)\r\nCrossFire (http://igrs.id/aplikasi/crossfire)\r\nSeal Online (http://igrs.id/aplikasi/seal-online)\r\nIdol Street 2 (http://igrs.id/aplikasi/idol-street-2)\r\nRohan Online (http://igrs.id/aplikasi/rohan-online)\r\n\r\nKarena saya ingin mengubah beberapa ketentuan di bagian pilihan Ratingnya. Saya ingi mengubahnya menjadi tergabung di kelompok usia 13Thn ke atas. \r\n\r\nTerima Kasih'),
(9, 'Teddy', 'teddy@lyto.net', '08568196437', 'Hapus Aplikasi', 'Halo\r\n\r\nMau minta tolong lagi untuk hapus yang ini biar kelihatan rapi lagi.\r\n1. Pixel Super Heroes yang hasil assesmentnya "Tidak diklarifikasikan"\r\n2. ROBOWAR - Robot VS Alien yang hasil assesmentnya " 3+"\r\n\r\nThanks\r\nBest regards\r\nTeddy'),
(10, 'Teddy', 'teddy@lyto.net', '08568196437', 'Hapus Konten Game Rating', 'Halo, saya ingin minta bantuannya untuk delete salah satu game yang kami daftarkan yaitu Idol Street 2 ( http://igrs.id/aplikasi/idol-street-2) karena ada kesalahan dalam pengisian assessment. Terima kasih.'),
(11, 'Roy', 'roy@lyto.net', '082110762518', 'Hapus Game', 'Halo, saya ingin meminta bantuannya untuk menghapus game Perfect World Online 2 Indones (http://igrs.id/aplikasi/perfect-world-online-2-indonesia) karena sudah tidak valid dan sudah didaftarkan sebelumnya. Terima kasih'),
(12, 'Fandi ahmad santoso putra', 'fandi.asap@gmail.com', '085716558817', 'Lowongan pekerjaan di IGRS.ID', 'Halo tim IGRS.ID,\r\n\r\nSaya Fandi, ingin bertanya apakah ada lowongan pekerjaan di IGRS.ID saat ini? Mohon informasinya jika ada, terimakasih.\r\n\r\nSalam,\r\nFandi'),
(13, 'Wita Risanti', 'witarisanti@gmail.com', '081388704939', 'Permainan Simulasi Judi?', 'Halo, saya ingin menanyakan mengenai salah satu ketentuan dalam PM No. 11 Tahun 2016, khususnya mengenai Pasal 9 huruf b yang menyatakan bahwa permainan tidak dapat diklasifikasikan apabila memuat konten yang "merupakan kegiatan judi yang dapat menggunakan uang asli ataupun uang virtual yang dapat ditukarkan menjadi uang asli".\r\n\r\nYang saya ingin tanyakan apakah "menggunakan uang asli ataupun uang virtual" dan "ditukarkan menjadi uang asli" ini kumulatif? Seandainya permainan merupakan simulasi judi dimana pemain dapat membeli chip dengan uang asli, tetapi tidak dapat ditukarkan kembali menjadi uang asli bagaimana ya? \r\n\r\nTerimakasih.'),
(14, 'M. Rahman M. T', 'fivemoms.feedback@gmail.com', '082332490002', 'Revisi upload', 'Selamat malam IGRS\r\nmas maaf mengganggu, saya salah upload informasi game saya di dashboard ada 2, apakah bisa saya edit ya mas atau buat uplod informasi lagi ya enakanya.\r\nmohon bantuannya IGRS . :-)');
